            {
                v_acsc_search_trinh_do.Add(dr[V_DM_DU_LIEU_NHAN_VIEN.TRINH_DO].ToString());
            }
            m_txt_trinh_do.AutoCompleteCustomSource = v_acsc_search_trinh_do;
