        {
            //	f307_RPT_NHAN_VIEN_CHUA_CHUC_VU_TRANG_THAI_DE v_fDE = new  f307_RPT_NHAN_VIEN_CHUA_CHUC_VU_TRANG_THAI_DE();								
            //	v_fDE.display();
            load_data_2_grid();
