		private void load_data_2_grid(){						
			m_ds = new DS_V_GD_QUA_TRINH_LAM_VIEC_2();			
			m_us.FillDataset(m_ds);
			m_fg.Redraw = false;
			CGridUtils.Dataset2C1Grid(m_ds, m_fg, m_obj_trans);
            m_fg.Subtotal(C1.Win.C1FlexGrid.AggregateEnum.Count
              , 0
              , (int)e_col_Number.MA_CV
              , (int)e_col_Number.TEN_CV
              , "{0}"
              );
			m_fg.Redraw = true;
		}
        private void load_data_2_grid_search()
        {
            decimal v_kiem_nhiem;
            if (m_ckb_kiem_nhiem.Checked)
                v_kiem_nhiem = -1;
            else
                v_kiem_nhiem = 650;
            m_obj_trans = get_trans_object(m_fg);
            m_ds.Clear();
            m_us.FillDatasetSearchChucVuThoiDiem(m_ds, ma_cv, m_dtp_thoidiem.Value.Date,CAppContext_201.getCurrentIDPhapnhan(), v_kiem_nhiem);
            m_fg.Redraw = false;
            CGridUtils.Dataset2C1Grid(m_ds, m_fg, m_obj_trans);
            if (m_rdb_nhom.Checked == true)
            {
                m_fg.Subtotal(C1.Win.C1FlexGrid.AggregateEnum.Count
              , 0
              , (int)e_col_Number.DIA_BAN
              , (int)e_col_Number.MA_NV
              , "{0}"
              );
                m_fg.Subtotal(C1.Win.C1FlexGrid.AggregateEnum.Count
              , 1
              , (int)e_col_Number.MA_CV
              , (int)e_col_Number.MA_NV
              , "{0}"
              );
            }
            m_fg.Redraw = true;
          //  m_lbl_soluongns.Text = m_ds.V_GD_QUA_TRINH_LAM_VIEC_2.Count.ToString();

            decimal count = 0;
            m_us.count_nhan_vien_theo_chuc_vu(m_ds, ma_cv, m_dtp_thoidiem.Value.Date,CAppContext_201.getCurrentIDPhapnhan(), ref count,v_kiem_nhiem);
            m_lbl_soluongns.Text = CIPConvert.ToStr(count);
        }
