        private void load_custom_source_2_m_txt_tim_kiem()
        {
            //m_v_us.FillDataset(m_v_ds);
            int count = m_ds.Tables["V_GD_QUA_TRINH_LAM_VIEC_2"].Rows.Count;
            AutoCompleteStringCollection v_acsc_search = new AutoCompleteStringCollection();
            foreach (DataRow dr in m_ds.V_GD_QUA_TRINH_LAM_VIEC_2)
            {
                v_acsc_search.Add(dr[V_GD_QUA_TRINH_LAM_VIEC_2.MA_CV].ToString());
                v_acsc_search.Add(dr[V_GD_QUA_TRINH_LAM_VIEC_2.MA_CV].ToString() + " - " + dr[V_GD_QUA_TRINH_LAM_VIEC_2.TEN_CV].ToString());
                v_acsc_search.Add(dr[V_GD_QUA_TRINH_LAM_VIEC_2.HO_DEM].ToString()+ " " + dr[V_GD_QUA_TRINH_LAM_VIEC_2.TEN].ToString());
                v_acsc_search.Add(dr[V_GD_QUA_TRINH_LAM_VIEC_2.MA_NV].ToString());
            }
            m_txt_tim_kiem.AutoCompleteCustomSource = v_acsc_search;
        }
        private void export_2_excel()
        {
            CExcelReport v_obj_excel_rpt = new CExcelReport("f402_bao_cao_nhan_su_chuc_vu.xlsx", 3, 1);
            v_obj_excel_rpt.AddFindAndReplaceItem("<thoi_diem>", m_dtp_thoidiem.Value.Date);
            v_obj_excel_rpt.FindAndReplace(false);
            v_obj_excel_rpt.Export2ExcelWithoutFixedRows(m_fg, 1, m_fg.Cols.Count - 1, true);
        }
