        private ITransferDataRow get_trans_object(C1.Win.C1FlexGrid.C1FlexGrid i_fg)
        {
            Hashtable v_htb = new Hashtable();
            v_htb.Add(V_GD_BAO_CAO_DU_AN_2.SL_THANH_VIEN, e_col_Number.SL_THANH_VIEN);
            v_htb.Add(V_GD_BAO_CAO_DU_AN_2.CO_CHE, e_col_Number.CO_CHE);
            v_htb.Add(V_GD_BAO_CAO_DU_AN_2.NGAY_KET_THUC, e_col_Number.NGAY_KET_THUC);
            v_htb.Add(V_GD_BAO_CAO_DU_AN_2.NGAY_BAT_DAU, e_col_Number.NGAY_BAT_DAU);
            v_htb.Add(V_GD_BAO_CAO_DU_AN_2.TEN_DU_AN, e_col_Number.TEN_DU_AN);
            v_htb.Add(V_GD_BAO_CAO_DU_AN_2.GHI_CHU, e_col_Number.GHI_CHU);
            v_htb.Add(V_GD_BAO_CAO_DU_AN_2.MA_DU_AN, e_col_Number.MA_DU_AN);
            v_htb.Add(V_GD_BAO_CAO_DU_AN_2.LOAI_DU_AN, e_col_Number.LOAI_DU_AN);
            v_htb.Add(V_GD_BAO_CAO_DU_AN_2.TRANG_THAI, e_col_Number.TRANG_THAI);
            v_htb.Add(V_GD_BAO_CAO_DU_AN_2.MA_QUYET_DINH, e_col_Number.MA_QUYET_DINH);
            v_htb.Add(V_GD_BAO_CAO_DU_AN_2.TRUONG_DU_AN, e_col_Number.TRUONG_DU_AN);
            v_htb.Add(V_GD_BAO_CAO_DU_AN_2.LOAI_QD, e_col_Number.LOAI_QD);

            ITransferDataRow v_obj_trans = new CC1TransferDataRow(i_fg, v_htb, m_ds.V_GD_BAO_CAO_DU_AN_2.NewRow());
            return v_obj_trans;
        }

        private void auto_suggest_text()
        {
            US_V_DM_DU_AN_QUYET_DINH_TU_DIEN v_us_v_dm_da_qd_td = new US_V_DM_DU_AN_QUYET_DINH_TU_DIEN();
            DS_V_DM_DU_AN_QUYET_DINH_TU_DIEN v_ds_v_dm_da_qd_td = new DS_V_DM_DU_AN_QUYET_DINH_TU_DIEN();
            v_us_v_dm_da_qd_td.FillDataset(v_ds_v_dm_da_qd_td);
            var v_acsc_search = new AutoCompleteStringCollection();
            foreach (DataRow dr in v_ds_v_dm_da_qd_td.V_DM_DU_AN_QUYET_DINH_TU_DIEN)
            {
                v_acsc_search.Add(dr[V_DM_DU_AN_QUYET_DINH_TU_DIEN.MA_DU_AN].ToString());
                v_acsc_search.Add(dr[V_DM_DU_AN_QUYET_DINH_TU_DIEN.TEN_DU_AN].ToString());
                v_acsc_search.Add(dr[V_DM_DU_AN_QUYET_DINH_TU_DIEN.MA_QUYET_DINH].ToString());
            }
            m_txt_tim_kiem.AutoCompleteCustomSource = v_acsc_search;
        }

        private void load_data_2_grid()
        {
            m_ds = new DS_V_GD_BAO_CAO_DU_AN_2();

            var v_str_search = m_txt_tim_kiem.Text.Trim();
            if (v_str_search == m_str_suggest)
                v_str_search = "";

            if (m_cbo_tim_kiem_theo_ngay.SelectedIndex == -1)
            {
                m_us.FillDataset(m_ds);
                m_fg.Redraw = false;
                CGridUtils.Dataset2C1Grid(m_ds, m_fg, m_obj_trans);
                m_fg.Redraw = true;
                m_lbl_total_record_grid1.Text = string.Format("Có {0} dự án", m_ds.Tables[0].Rows.Count);
                return;
            }
            if (m_cbo_tim_kiem_theo_ngay.SelectedIndex == 0)
                m_us.FillDatasetSearch(m_ds,
                                        v_str_search,
                                        m_dat_tu_ngay.Value,
                                        m_dat_den_ngay.Value,
                                        decimal.Parse(m_cbo_trang_thai.SelectedValue.ToString()),
                                        decimal.Parse(m_cbo_loai_du_an.SelectedValue.ToString()),
                                        decimal.Parse(m_cbo_co_che.SelectedValue.ToString()),
                                        0);
            if (m_cbo_tim_kiem_theo_ngay.SelectedIndex == 1)
                m_us.FillDatasetSearch(m_ds,
                                        v_str_search,
                                        m_dat_tu_ngay.Value,
                                        m_dat_den_ngay.Value,
                                        decimal.Parse(m_cbo_trang_thai.SelectedValue.ToString()),
                                        decimal.Parse(m_cbo_loai_du_an.SelectedValue.ToString()),
                                        decimal.Parse(m_cbo_co_che.SelectedValue.ToString()),
                                        1);
            if (m_cbo_tim_kiem_theo_ngay.SelectedIndex == 2)
                m_us.FillDatasetSearch(m_ds,
                                        v_str_search,
                                        m_dat_tu_ngay.Value,
                                        m_dat_den_ngay.Value,
                                        decimal.Parse(m_cbo_trang_thai.SelectedValue.ToString()),
                                        decimal.Parse(m_cbo_loai_du_an.SelectedValue.ToString()),
                                        decimal.Parse(m_cbo_co_che.SelectedValue.ToString()),
                                        2);

            m_fg.Redraw = false;
            CGridUtils.Dataset2C1Grid(m_ds, m_fg, m_obj_trans);
            m_fg.Redraw = true;
            m_lbl_total_record_grid1.Text = string.Format("Có {0} dự án", m_ds.Tables[0].Rows.Count);
        }
        private void grid2us_object(US_V_GD_BAO_CAO_DU_AN_2 i_us
            , int i_grid_row)
        {
            DataRow v_dr;
            v_dr = (DataRow)m_fg.Rows[i_grid_row].UserData;
            m_obj_trans.GridRow2DataRow(i_grid_row, v_dr);
            i_us.DataRow2Me(v_dr);
        }


        private void us_object2grid(US_V_GD_BAO_CAO_DU_AN_2 i_us
            , int i_grid_row)
        {
            DataRow v_dr = (DataRow)m_fg.Rows[i_grid_row].UserData;
            i_us.Me2DataRow(v_dr);
            m_obj_trans.DataRow2GridRow(v_dr, i_grid_row);
        }


