                m_txt_ngay_sinh.Text = m_us.datNGAY_SINH.ToShortDateString();
            else
                m_txt_ngay_sinh.Text = "";
            m_txt_noi_sinh.Text = m_us.strNOI_SINH;
            m_txt_nguyen_quan.Text = m_us.strNGUYEN_QUAN;
            m_txt_cmnd.Text = m_us.strCMND;
