                    + v_str_ngay_het_hieu_luc;
            }
            else
            {
                m_txt_chuc_vu.Text = "";
                m_txt_don_vi.Text = "";
                m_txt_dia_ban.Text = "";
                m_txt_loai_don_vi.Text = "";
                m_txt_ma_quyet_dinh.Text = "";
                m_txt_hieu_luc_chuc_vu.Text = "";
