            }
            catch (Exception v_e)
            {
                
                CSystemLog_301.ExceptionHandle(v_e);
            }
        }

		private void m_cmd_delete_Click(object sender, EventArgs e) {
			try{
				delete_dm_headcount();
			}
			catch (Exception v_e){
				CSystemLog_301.ExceptionHandle(v_e);
			}
		}

