        // Event private methods
        private void load_file_excel()
        {
            if (m_ofd_exel_file.ShowDialog() == System.Windows.Forms.DialogResult.OK)
            {
                try
                {
                    m_lbl_loading_mes.Visible = true;
                    CExcelReport v_excel_rpt = new CExcelReport(m_ofd_exel_file.FileName);

                    m_ds.EnforceConstraints = false;

                    v_excel_rpt.Export2DatasetDS_by_DucVT(m_ds, m_ds.DM_NHAN_SU.TableName, 2);

                    m_fg.Redraw = false;
                    CGridUtils.Dataset2C1Grid(m_ds, m_fg, m_obj_trans);
                    m_fg.Redraw = true;
                    m_lbl_loading_mes.Visible = false;
                }
                catch (Exception v_e)
                {
                    CSystemLog_301.ExceptionHandle(v_e);
                }
            }
        }

        private void kiem_tra_du_lieu()
        {
            if (m_ds != null)
            {
                // Tổ đỏ nền và cho phép sửa dữ liệu dòng đó.
                foreach (Row v_row in m_fg.Rows)
                {
                    // Kiểm tra dữ liệu trống trong các cột mã nv, họ đệm, tên
                    if (((string)v_row[DM_NHAN_SU.MA_NV]) == ""
                        || ((string)v_row[DM_NHAN_SU.HO_DEM]) == ""
                        || ((string)v_row[DM_NHAN_SU.TEN]) == "")
                    {
                        v_row.StyleDisplay.BackColor = System.Drawing.Color.Red;
                        v_row.AllowEditing = true;
                    }

                    // Kiểm tra dữ liệu trùng ở cột mã nv
                    
                }
            }
        }

        private void save_data()
        {
            // Ý tưởng Version 2. 
            // Bắt try catch ngay trong mỗi vòng lặp, nếu bị exception thì ghi lại m_us bị lỗi, sau đó cứ tiếp tục insert bản ghi tiếp theo.

            for (int i_grid_row = 1; i_grid_row < m_fg.Rows.Count; i_grid_row++)
            {
                grid2us_object(m_us, i_grid_row);
                m_us.Insert();
            }

            HelperDucVT.CUtils.show_success_message("Lưu dữ liệu nhân sự thành công");
        }
