		private void delete_v_gd_trang_thai_lao_dong(){
			if (!CGridUtils.IsThere_Any_NonFixed_Row(m_grv_trang_thai_lao_dong)) return;
			if (!CGridUtils.isValid_NonFixed_RowIndex(m_grv_trang_thai_lao_dong, m_grv_trang_thai_lao_dong.Row)) return;
			if (BaseMessages.askUser_DataCouldBeDeleted(8) != BaseMessages.IsDataCouldBeDeleted.CouldBeDeleted)  return;
			US_V_GD_TRANG_THAI_LAO_DONG v_us = new US_V_GD_TRANG_THAI_LAO_DONG();
			grid2us_object(v_us, m_grv_trang_thai_lao_dong.Row);
			try {			
				v_us.BeginTransaction();    											
				v_us.Delete();                      								
				v_us.CommitTransaction();
				m_grv_trang_thai_lao_dong.Rows.Remove(m_grv_trang_thai_lao_dong.Row);				
			}
			catch (Exception v_e) {
				v_us.Rollback();
				CDBExceptionHandler v_objErrHandler = new CDBExceptionHandler(v_e,
					new CDBClientDBExceptionInterpret());
				v_objErrHandler.showErrorMessage();
			}
		}

