        private void load_data_2_grid()
        {
            m_ds = new DS_V_GD_LUONG_THEO_QD();

            // Init id ky
            Decimal v_id_ky;
            try
            {
                v_id_ky = (Decimal)m_cbo_ky.SelectedValue;
            }
            catch (Exception)
            {
                v_id_ky = -1;
            }

            // Init id loai luong
            Decimal v_id_loai_luong;
            try
            {
                v_id_loai_luong = (Decimal)m_cbo_loai_luong.SelectedValue;
            }
            catch (Exception)
            {
                v_id_loai_luong = -1;
            }
            

            m_us.FillDatasetSearch(m_ds, 
                                    get_id_phap_nhan(), 
                                    m_txt_search.Text, 
                                    m_ckb_luong_hien_tai.Checked,
                                    v_id_ky,
                                    v_id_loai_luong,
                                    m_dat_ap_dung_tu_ngay.Value, m_dat_ap_dung_den_ngay.Value);
            m_fg.Redraw = false;
            CGridUtils.Dataset2C1Grid(m_ds, m_fg, m_obj_trans);

            m_fg.Sort(SortFlags.Ascending, (Int32)e_col_Number.MA_NV);

            m_fg.Subtotal(C1.Win.C1FlexGrid.AggregateEnum.Count
                , 0
                , (int)e_col_Number.MA_NV
                , (int)e_col_Number.LUONG
                , "{0}"
                );

            m_fg.Redraw = true;
            m_fg.Focus();

            // Load grid trước thì mới có dữ liệu
            load_data_2_suggest_txt();
        }

        private void grid2us_object(US_V_GD_LUONG_THEO_QD i_us
            , int i_grid_row)
        {
            DataRow v_dr;
            v_dr = (DataRow)m_fg.Rows[i_grid_row].UserData;
            m_obj_trans.GridRow2DataRow(i_grid_row, v_dr);
            i_us.DataRow2Me(v_dr);
        }


        private void us_object2grid(US_V_GD_LUONG_THEO_QD i_us
            , int i_grid_row)
        {
            DataRow v_dr = (DataRow)m_fg.Rows[i_grid_row].UserData;
            i_us.Me2DataRow(v_dr);
            m_obj_trans.DataRow2GridRow(v_dr, i_grid_row);
        }


        private void insert_v_gd_luong_theo_qd()
        {
            f301_V_GD_LUONG_THEO_QD_DE v_fDE = new  f301_V_GD_LUONG_THEO_QD_DE();								
            v_fDE.display_for_insert();
            load_data_2_grid();
        }

        private void update_v_gd_luong_theo_qd()
        {
            if (!CGridUtils.IsThere_Any_NonFixed_Row(m_fg)) return;
            if (!CGridUtils.isValid_NonFixed_RowIndex(m_fg, m_fg.Row)) return;
            grid2us_object(m_us, m_fg.Row);
            f301_V_GD_LUONG_THEO_QD_DE v_fDE = new f301_V_GD_LUONG_THEO_QD_DE();
            v_fDE.display_for_update(m_us);
            load_data_2_grid();
        }

        private void delete_v_gd_luong_theo_qd()
        {
            // Check validation
            if (!CGridUtils.IsThere_Any_NonFixed_Row(m_fg)) return;
            if (!CGridUtils.isValid_NonFixed_RowIndex(m_fg, m_fg.Row)) return;
            if (BaseMessages.askUser_DataCouldBeDeleted(8) != BaseMessages.IsDataCouldBeDeleted.CouldBeDeleted) return;
            
            // Init
            US_V_GD_LUONG_THEO_QD v_us_v_gd = new US_V_GD_LUONG_THEO_QD();

            // Grid 2 us
            grid2us_object(v_us_v_gd, m_fg.Row);

            try
            {
                m_fg.Redraw = false;
                v_us_v_gd.BeginTransaction();
                v_us_v_gd.DeleteByID(v_us_v_gd.dcID_QUYET_DINH);
                v_us_v_gd.CommitTransaction();
                m_fg.Rows.Remove(m_fg.Row);
                m_fg.Redraw = true;
            }
            catch (Exception v_e)
            {
                v_us_v_gd.Rollback();
                CDBExceptionHandler v_objErrHandler = new CDBExceptionHandler(v_e,
                    new CDBClientDBExceptionInterpret());
                v_objErrHandler.showErrorMessage();
            }
        }

        private void view_v_gd_luong_theo_qd()
        {
            if (!CGridUtils.IsThere_Any_NonFixed_Row(m_fg)) return;
            if (!CGridUtils.isValid_NonFixed_RowIndex(m_fg, m_fg.Row)) return;
            grid2us_object(m_us, m_fg.Row);
            //	f301_V_GD_LUONG_THEO_QD_DE v_fDE = new f301_V_GD_LUONG_THEO_QD_DE();			
            //	v_fDE.display(m_us);
        }

