        // ~ DucVT ~
        /// <summary>
        /// Hàm format controls tự tạo bởi DucVT
        /// </summary>
        private void custom_format_controls()
        {
            m_txt_search.AutoCompleteMode = AutoCompleteMode.SuggestAppend;
            m_txt_search.AutoCompleteSource = AutoCompleteSource.CustomSource;

        }

        private void load_data_2_suggest_txt()
        {
            AutoCompleteStringCollection v_cus_source = new AutoCompleteStringCollection();

            foreach (DataRow v_dr in m_ds.Tables[0].Rows)
            {
                v_cus_source.Add((String)v_dr[V_GD_LUONG_THEO_QD.MA_NV]);
                v_cus_source.Add((String)v_dr[V_GD_LUONG_THEO_QD.MA_QD]);
                //v_cus_source.Add((String)v_dr[V_GD_LUONG_THEO_QD.HO_DEM]);
                v_cus_source.Add((String)v_dr[V_GD_LUONG_THEO_QD.TEN]);
                v_cus_source.Add((String)v_dr[V_GD_LUONG_THEO_QD.HO_DEM] + " " + (String)v_dr[V_GD_LUONG_THEO_QD.TEN]);
            }

            m_txt_search.AutoCompleteCustomSource = v_cus_source;
        }

        private void load_data_2_cbo()
        {
            // Cbo ma ky
            US_DM_KY v_us_dm_ky = new US_DM_KY();
            DS_DM_KY v_ds_dm_ky = new DS_DM_KY();

            v_us_dm_ky.FillDataset(v_ds_dm_ky);

            DataTable v_dt = v_ds_dm_ky.Tables[0];
            DataRow v_dr = v_dt.NewRow();

            v_dr[DM_KY.ID] = -1;
            v_dr[DM_KY.MA_KY] = "Tất cả";

            v_dt.Rows.InsertAt(v_dr, 0);

            m_cbo_ky.DataSource = v_dt;
            m_cbo_ky.ValueMember = DM_KY.ID;
            m_cbo_ky.DisplayMember = DM_KY.MA_KY;


            // Cbo loai luong
            CUtils.load_datasource_loai_quyet_dinh(m_cbo_loai_luong, 1, true);

        }

