        private ITransferDataRow get_trans_object(C1.Win.C1FlexGrid.C1FlexGrid i_fg)
        {
            Hashtable v_htb = new Hashtable();
            v_htb.Add(IMPORT_EXCEL_LUONG_THEO_QD.LUONG, e_col_Number.LUONG);
            v_htb.Add(IMPORT_EXCEL_LUONG_THEO_QD.MA_NV, e_col_Number.MA_NV);
            v_htb.Add(IMPORT_EXCEL_LUONG_THEO_QD.NGAY_KY, e_col_Number.NGAY_KY);
            v_htb.Add(IMPORT_EXCEL_LUONG_THEO_QD.NGAY_HET_HIEU_LUC, e_col_Number.NGAY_HET_HIEU_LUC);
            v_htb.Add(IMPORT_EXCEL_LUONG_THEO_QD.NOI_DUNG, e_col_Number.NOI_DUNG);
            v_htb.Add(IMPORT_EXCEL_LUONG_THEO_QD.HO_TEN, e_col_Number.HO_TEN);
            v_htb.Add(IMPORT_EXCEL_LUONG_THEO_QD.NGAY_AP_DUNG, e_col_Number.NGAY_AP_DUNG);
            v_htb.Add(IMPORT_EXCEL_LUONG_THEO_QD.LINK, e_col_Number.LINK);
            //v_htb.Add(IMPORT_EXCEL_LUONG_THEO_QD.MA_KY, e_col_Number.MA_KY);
            v_htb.Add(IMPORT_EXCEL_LUONG_THEO_QD.MA_QD, e_col_Number.MA_QD);
            v_htb.Add(IMPORT_EXCEL_LUONG_THEO_QD.NGAY_CO_HIEU_LUC, e_col_Number.NGAY_CO_HIEU_LUC);

            ITransferDataRow v_obj_trans = new CC1TransferDataRow(i_fg, v_htb, m_ds.IMPORT_EXCEL_LUONG_THEO_QD.NewRow());
            return v_obj_trans;
        }

        private void load_data_2_grid()
        {
            m_ds = new DS_IMPORT_EXCEL_LUONG_THEO_QD();
            m_us.FillDataset(m_ds);
            m_fg.Redraw = false;
            CGridUtils.Dataset2C1Grid(m_ds, m_fg, m_obj_trans);
            m_fg.Styles[CellStyleEnum.Normal].WordWrap = true;
            m_fg.AllowResizing = AllowResizingEnum.Rows;
            m_fg.AutoSizeRows(); m_fg.Styles[CellStyleEnum.Normal].WordWrap = true;
            m_fg.AllowResizing = AllowResizingEnum.Rows;
            m_fg.AutoSizeRows();

            m_fg.Redraw = true;
        }
        private void grid2us_object(US_IMPORT_EXCEL_LUONG_THEO_QD i_us
            , int i_grid_row)
        {
            DataRow v_dr;
            v_dr = (DataRow)m_fg.Rows[i_grid_row].UserData;
            m_obj_trans.GridRow2DataRow(i_grid_row, v_dr);
            i_us.DataRow2Me(v_dr);
