        private void load_custom_source_2_m_txt_tim_kiem()
        {
            //m_us.FillDataset(m_ds);
            int count = m_ds.Tables["DM_CAP_BAC"].Rows.Count;
            for (int i = 0; i < count; i++)
            {
                DataRow dr = m_ds.Tables["DM_CAP_BAC"].Rows[i];
                m_txt_tim_kiem.AutoCompleteCustomSource.Add(dr[2].ToString());
                m_txt_tim_kiem.AutoCompleteCustomSource.Add(dr[1].ToString());


            }
        }
        private void format_controls()
        {
            CControlFormat.setFormStyle(this, new CAppContext_201());
            CControlFormat.setC1FlexFormat(m_fg);
            CGridUtils.AddSave_Excel_Handlers(m_fg);
            CGridUtils.AddSearch_Handlers(m_fg);

            
            set_define_events();
            this.KeyPreview = true;
        }
        private void set_initial_form_load()
        {
            m_obj_trans = get_trans_object(m_fg);
            load_data_2_grid();
        }
        private ITransferDataRow get_trans_object(C1.Win.C1FlexGrid.C1FlexGrid i_fg)
        {
            Hashtable v_htb = new Hashtable();
            v_htb.Add(DM_CAP_BAC.MA_CAP, e_col_Number.MA_CAP);
            v_htb.Add(DM_CAP_BAC.MA_BAC,e_col_Number.MA_BAC);
            v_htb.Add(DM_CAP_BAC.NGAY_AP_DUNG, e_col_Number.NGAY_AP_DUNG);
            v_htb.Add(DM_CAP_BAC.NGAY_KET_THUC, e_col_Number.NGAY_KET_THUC);
            v_htb.Add(DM_CAP_BAC.TRANG_THAI, e_col_Number.TRANG_THAI);

            ITransferDataRow v_obj_trans = new CC1TransferDataRow(i_fg, v_htb, m_ds.DM_CAP_BAC.NewRow());
            return v_obj_trans;
        }
        private void load_data_2_grid()
        {
            m_ds = new DS_DM_CAP_BAC();

            m_us.FillDatasetSearch(m_ds, m_txt_tim_kiem.Text.Trim());
            //m_us.FillDataset(m_ds);
            var v_str_search = m_txt_tim_kiem.Text.Trim();
            if (v_str_search.Equals(m_str_tim_kiem))
            {
                v_str_search = "";
            }
            m_fg.Redraw = false;
            CGridUtils.Dataset2C1Grid(m_ds, m_fg, m_obj_trans);
           
            m_fg.Redraw = true;
            set_search_format_before();
        }

        private void set_search_format_before()
        {
            if (m_txt_tim_kiem.Text == "")
            {
                m_txt_tim_kiem.Text = m_str_tim_kiem;
                m_txt_tim_kiem.ForeColor = Color.Gray;
            }
        }

        private void set_search_format_after()
        {
            if (m_txt_tim_kiem.Text == m_str_tim_kiem)
            {
                m_txt_tim_kiem.Text = "";
            }
            m_txt_tim_kiem.ForeColor = Color.Black;
        }
        private void us_object2grid(US_DM_CAP_BAC i_us
            , int i_grid_row)
        {
            DataRow v_dr = (DataRow)m_fg.Rows[i_grid_row].UserData;
            i_us.Me2DataRow(v_dr);
            m_obj_trans.DataRow2GridRow(v_dr, i_grid_row);
        }
        private void insert_dm_cap_bac()
        {
            F603_dm_cap_bac_de v_fDE = new F603_dm_cap_bac_de();
            v_fDE.display_for_insert();
            m_txt_tim_kiem.Text = "";
            load_data_2_grid();
            m_txt_tim_kiem.Text = m_str_tim_kiem;
        }
        private void update_dm_cap_bac()
        {
            if (!CGridUtils.IsThere_Any_NonFixed_Row(m_fg)) return;
            if (!CGridUtils.isValid_NonFixed_RowIndex(m_fg, m_fg.Row)) return;
            grid2us_object(m_us, m_fg.Row);
            //F603_dm_cap_bac_de v_fDE = new F603_dm_cap_bac_de();
            //v_fDE.display_for_update(m_us);
            m_txt_tim_kiem.Text = "";
            load_data_2_grid();
            m_txt_tim_kiem.Text = m_str_tim_kiem;
        }
        private void grid2us_object(US_DM_CAP_BAC i_us
            , int i_grid_row)
        {
            DataRow v_dr;
            v_dr = (DataRow)m_fg.Rows[i_grid_row].UserData;
            m_obj_trans.GridRow2DataRow(i_grid_row, v_dr);
            i_us.DataRow2Me(v_dr);
        }
        private void delete_dm_cap_bac()
        {
            if (!CGridUtils.IsThere_Any_NonFixed_Row(m_fg)) return;
            if (!CGridUtils.isValid_NonFixed_RowIndex(m_fg, m_fg.Row)) return;
            if (BaseMessages.askUser_DataCouldBeDeleted(8) != BaseMessages.IsDataCouldBeDeleted.CouldBeDeleted) return;
            US_DM_CAP_BAC v_us = new US_DM_CAP_BAC();
            grid2us_object(m_us, m_fg.Row);
            try
            {
                v_us.BeginTransaction();
                v_us.Delete();
                v_us.CommitTransaction();
                m_fg.Rows.Remove(m_fg.Row);
            }
            catch (Exception v_e)
            {
                v_us.Rollback();
                CDBExceptionHandler v_objErrHandler = new CDBExceptionHandler(v_e,
                    new CDBClientDBExceptionInterpret());
                v_objErrHandler.showErrorMessage();
            }
        }

        private void view_dm_cap_bac()
        {
            if (!CGridUtils.IsThere_Any_NonFixed_Row(m_fg)) return;
            if (!CGridUtils.isValid_NonFixed_RowIndex(m_fg, m_fg.Row)) return;
            grid2us_object(m_us, m_fg.Row);
            F603_dm_cap_bac_de v_fDE = new F603_dm_cap_bac_de();
            //v_fDE.display(m_us);
        }

        private void select_data_2_us()
        {
            if (!CGridUtils.IsThere_Any_NonFixed_Row(m_fg)) return;
            if (!CGridUtils.isValid_NonFixed_RowIndex(m_fg, m_fg.Row)) return;
            grid2us_object(m_us, m_fg.Row);
            this.Close();
        }
