        private void m_fg_KeyDown(object sender, KeyEventArgs e)
        {
            try
            {
                switch (e.KeyCode)
                {
                    case Keys.Enter:
                        if (m_fg.Focused)
                        {
                            switch (m_e_form_mode)
                            {

                                case DataEntryFormMode.ViewDataState:
                                    update_dm_cap_bac();
                                    break;
                                case DataEntryFormMode.SelectDataState:
                                    select_data_2_us();
                                    break;

                            }
                        }
                        break;
                }
            }
            catch (Exception v_e)
            {
                CSystemLog_301.ExceptionHandle(v_e);
            }
        }




