        private void load_data_2_grid_du_an()
        {
            m_ds_du_an = new DS_V_DM_DU_AN_QUYET_DINH_TU_DIEN();

            var v_str_search = m_txt_tim_kiem.Text.Trim();
            if (v_str_search == m_str_suggest)
                v_str_search = "";

            if (m_cbo_tim_kiem_theo_ngay.SelectedIndex == -1)
                return;

            m_us_du_an.FillDatasetSearch(m_ds_du_an,
                                        m_i_is_report,
                                        v_str_search,
                                        m_dat_tu_ngay.Value,
                                        m_dat_den_ngay.Value,
                                        decimal.Parse(m_cbo_trang_thai.SelectedValue.ToString()),
                                        decimal.Parse(m_cbo_loai_du_an.SelectedValue.ToString()),
                                        decimal.Parse(m_cbo_co_che.SelectedValue.ToString()),
                                        m_cbo_tim_kiem_theo_ngay.SelectedIndex,
                                        CAppContext_201.getCurrentIDPhapnhan(),
                                        (decimal)e_tu_dien.QĐ_THANH_LAP_DU_AN);

            m_fg_du_an.Redraw = false;
            CGridUtils.Dataset2C1Grid(m_ds_du_an, m_fg_du_an, m_obj_trans_du_an);
            m_fg_du_an.Redraw = true;
            m_lbl_total_record_grid1.Text = string.Format("Có {0} dự án", m_ds_du_an.Tables[0].Rows.Count);
            auto_suggest_text();
        }

        private void load_data_2_grid_nhan_su()
        {
            if (!CGridUtils.IsThere_Any_NonFixed_Row(m_fg_du_an)) return;
            if (!CGridUtils.isValid_NonFixed_RowIndex(m_fg_du_an, m_fg_du_an.Row)) return;
            grid2us_object_du_an(m_us_du_an, m_fg_du_an.Row);
            m_ds_nhan_su = new DS_V_DM_NHAN_SU_DU_AN();
            m_us_nhan_su.FillDatasetByIdDuAn(m_ds_nhan_su, m_us_du_an.dcID, CAppContext_201.getCurrentIDPhapnhan());
            m_fg_nhan_su.Redraw = false;
            CGridUtils.Dataset2C1Grid(m_ds_nhan_su, m_fg_nhan_su, m_obj_trans_nhan_su);
            m_fg_nhan_su.Redraw = true;
            m_lbl_total_record_grid2.Text = string.Format("Có {0} thành viên trong dự án", m_ds_nhan_su.Tables[0].Rows.Count);
        }

        private void grid2us_object_du_an(US_V_DM_DU_AN_QUYET_DINH_TU_DIEN i_us, int i_grid_row)
        {
            DataRow v_dr;
            v_dr = (DataRow)m_fg_du_an.Rows[i_grid_row].UserData;
            if (v_dr == null) return;
            m_obj_trans_du_an.GridRow2DataRow(i_grid_row, v_dr);
            i_us.DataRow2Me(v_dr);
        }

        private void grid2us_object_nhan_su(US_V_DM_NHAN_SU_DU_AN i_us, int i_grid_row)
        {
            DataRow v_dr;
            v_dr = (DataRow)m_fg_nhan_su.Rows[i_grid_row].UserData;
            m_obj_trans_nhan_su.GridRow2DataRow(i_grid_row, v_dr);
            i_us.DataRow2Me(v_dr);
        }

        private void us_object2grid(US_V_DM_DU_AN_QUYET_DINH_TU_DIEN i_us, int i_grid_row)
        {
            DataRow v_dr = (DataRow)m_fg_du_an.Rows[i_grid_row].UserData;
            i_us.Me2DataRow(v_dr);
            m_obj_trans_du_an.DataRow2GridRow(v_dr, i_grid_row);
        }

        private void insert_du_an()
        {
            f500_dm_du_an_detail v_fDE = new f500_dm_du_an_detail();
            v_fDE.display();
            load_data_2_grid_du_an();
            load_data_2_grid_nhan_su();
            m_fg_du_an.Select(m_fg_du_an.FindRow(v_fDE.lay_ma_du_an_vua_insert(), 1, (int)e_col_Number_du_an.MA_DU_AN, true), (int)e_col_Number_du_an.MA_DU_AN);
        }

        private void update_v_dm_du_an_quyet_dinh_tu_dien()
        {
            var currentRow = 0;
            if (!CGridUtils.IsThere_Any_NonFixed_Row(m_fg_du_an)) return;
            if (!CGridUtils.isValid_NonFixed_RowIndex(m_fg_du_an, m_fg_du_an.Row)) return;
            currentRow = m_fg_du_an.Row;
            grid2us_object_du_an(m_us_du_an, m_fg_du_an.Row);

            f500_dm_du_an_detail v_fDE = new f500_dm_du_an_detail();
            v_fDE.set_us_du_an(m_us_du_an);
            v_fDE.display();
            load_data_2_grid_du_an();
            load_data_2_grid_nhan_su();
            m_fg_du_an.Select(currentRow, (int)e_col_Number_du_an.MA_DU_AN);
        }

        private void delete_du_an()
        {
            if (!CGridUtils.IsThere_Any_NonFixed_Row(m_fg_du_an)) return;
            if (!CGridUtils.isValid_NonFixed_RowIndex(m_fg_du_an, m_fg_du_an.Row)) return;
            grid2us_object_du_an(m_us_du_an, m_fg_du_an.Row);

            US_GD_CHI_TIET_DU_AN v_us_ct_da = new US_GD_CHI_TIET_DU_AN();
            DS_GD_CHI_TIET_DU_AN v_ds_ct_da = new DS_GD_CHI_TIET_DU_AN();
            v_us_ct_da.FillDataset(v_ds_ct_da, string.Format("WHERE {0} = {1}",
                                                                GD_CHI_TIET_DU_AN.ID_DU_AN,
                                                                m_us_du_an.dcID));
            if (v_ds_ct_da.GD_CHI_TIET_DU_AN.Rows.Count > 0 && BaseMessages.MsgBox_Confirm("Đang có nhân viên trong dự án. Bạn có chắc chắn muốn xóa không?"))
            {
                delete_quyet_dinh_cua_du_an(new US_DM_DU_AN(m_us_du_an.dcID));
            }
            if (v_ds_ct_da.GD_CHI_TIET_DU_AN.Rows.Count == 0 && BaseMessages.MsgBox_Confirm("Bạn có chắc chắn muốn xóa dự án này không?"))
            {
                delete_quyet_dinh_cua_du_an(new US_DM_DU_AN(m_us_du_an.dcID));
            }
            load_data_2_grid_du_an();
            load_data_2_grid_nhan_su();
            m_fg_du_an.Select(1, (int)e_col_Number_du_an.MA_DU_AN);
        }

        private void delete_quyet_dinh_cua_du_an(US_DM_DU_AN ip_us)
        {
            try
            {
                ip_us.BeginTransaction();
                US_GD_QUYET_DINH_DU_AN v_us_qd_du_an = new US_GD_QUYET_DINH_DU_AN();
                DS_GD_QUYET_DINH_DU_AN v_ds_qd_du_an = new DS_GD_QUYET_DINH_DU_AN();
                v_us_qd_du_an.FillDataset(v_ds_qd_du_an, string.Format("WHERE {0} = {1}",
                                                                        GD_QUYET_DINH_DU_AN.ID_DU_AN,
                                                                        ip_us.dcID));
                if (v_ds_qd_du_an.GD_QUYET_DINH_DU_AN.Rows.Count > 0)
                {
                    for (int i = 0; i < v_ds_qd_du_an.GD_QUYET_DINH_DU_AN.Rows.Count; i++)
                    {
                        var v_dc_id_quyet_dinh =
                            (decimal) v_ds_qd_du_an.GD_QUYET_DINH_DU_AN.Rows[i][GD_QUYET_DINH_DU_AN.ID_QUYET_DINH];
                        US_GD_QUYET_DINH_PHAP_NHAN v_us_qd_pn = new US_GD_QUYET_DINH_PHAP_NHAN();
                        v_us_qd_pn.UseTransOfUSObject(ip_us);
                        v_us_qd_pn.DeleteByID(v_dc_id_quyet_dinh);

                        v_us_qd_du_an.UseTransOfUSObject(ip_us);
                        v_us_qd_du_an.DeleteByID(v_dc_id_quyet_dinh);

                        US_GD_CHI_TIET_DU_AN v_us_ct_da = new US_GD_CHI_TIET_DU_AN();
                        v_us_ct_da.UseTransOfUSObject(ip_us);
                        v_us_ct_da.DeleteByID(v_dc_id_quyet_dinh);

                        US_DM_QUYET_DINH v_us_qd = new US_DM_QUYET_DINH();
                        v_us_qd.UseTransOfUSObject(ip_us);
                        v_us_qd.DeleteByID(v_dc_id_quyet_dinh);
                    }
                    ip_us.Delete();
                }
                ip_us.CommitTransaction();
            }
            catch (Exception)
            {
                if (ip_us.is_having_transaction())
                    ip_us.Rollback();
                throw;
            }
        }

