            v_htb.Add(V_GD_CONG_TAC.DIA_DIEM, e_col_Number.DIA_DIEM);
            v_htb.Add(V_GD_CONG_TAC.MA_NV, e_col_Number.MA_NV);
            v_htb.Add(V_GD_CONG_TAC.NGAY_VE, e_col_Number.NGAY_VE);
            v_htb.Add(V_GD_CONG_TAC.NGAY_KY, e_col_Number.NGAY_KY);
            v_htb.Add(V_GD_CONG_TAC.HO_DEM, e_col_Number.HO_DEM);
            v_htb.Add(V_GD_CONG_TAC.TEN, e_col_Number.TEN);
            v_htb.Add(V_GD_CONG_TAC.NOI_DUNG, e_col_Number.NOI_DUNG);
            v_htb.Add(V_GD_CONG_TAC.LOAI_QD, e_col_Number.LOAI_QD);
            v_htb.Add(V_GD_CONG_TAC.MA_QUYET_DINH, e_col_Number.MA_QUYET_DINH);

            ITransferDataRow v_obj_trans = new CC1TransferDataRow(i_fg, v_htb, m_ds.V_GD_CONG_TAC.NewRow());
            return v_obj_trans;
        }

        private void load_data_2_grid()
        {
            m_ds = new DS_V_GD_CONG_TAC();
            m_fg.Redraw = false;
            var v_id_phap_nhan = CAppContext_201.getCurrentIDPhapnhan();
            var v_str_search = m_txt_tim_kiem.Text.Trim();
            if (v_str_search == m_str_suggest)
                v_str_search = "";

                m_us.FillDatasetSearch(m_ds,
                                       v_str_search,
                                       m_dat_tu_ngay.Value,
                                       m_dat_den_ngay.Value,
                                       m_cbo_tim_kiem_theo.SelectedIndex,
                                       v_id_phap_nhan);
            
            CGridUtils.Dataset2C1Grid(m_ds, m_fg, m_obj_trans);
            m_fg.Subtotal(C1.Win.C1FlexGrid.AggregateEnum.Count // chỗ này dùng hàm count tức là để đếm, có thể dùng các hàm khác thay thế
              , 0
              , (int)e_col_Number.MA_QUYET_DINH // chỗ này là tên trường mà mình nhóm
              , (int)e_col_Number.MA_NV // chỗ này là tên trường mà mình Count
              , "{0}"
              );
            m_lbl_total_record.Text = string.Format("Có {0} bản ghi", m_ds.V_GD_CONG_TAC.Rows.Count);
            m_fg.Focus();
            m_fg.Redraw = true;

            auto_suggest_text();
        }

        private void grid2us_object(US_V_GD_CONG_TAC i_us, int i_grid_row)
        {
            DataRow v_dr;
            v_dr = (DataRow)m_fg.Rows[i_grid_row].UserData;
            m_obj_trans.GridRow2DataRow(i_grid_row, v_dr);
            i_us.DataRow2Me(v_dr);
        }

        private void us_object2grid(US_V_GD_CONG_TAC i_us, int i_grid_row)
        {
            DataRow v_dr = (DataRow)m_fg.Rows[i_grid_row].UserData;
            i_us.Me2DataRow(v_dr);
            m_obj_trans.DataRow2GridRow(v_dr, i_grid_row);
        }

        private void insert_v_gd_cong_tac()
        {
            f206_v_gd_cong_tac_de v_fDE = new f206_v_gd_cong_tac_de();
            v_fDE.display_for_insert();
            load_data_2_grid();
            m_fg.Select(m_fg.FindRow(v_fDE.lay_ma_quyet_dinh_vua_insert(), 1, (int)e_col_Number.MA_QUYET_DINH, true), (int)e_col_Number.MA_QUYET_DINH);
        }

        private void update_v_gd_cong_tac()
        {
            var currentRow = 0;
            if (!CGridUtils.IsThere_Any_NonFixed_Row(m_fg)) return;
            if (!CGridUtils.isValid_NonFixed_RowIndex(m_fg, m_fg.Row)) return;
            if (m_fg.Rows[m_fg.Row].IsNode) return;
            currentRow = m_fg.Row;
            grid2us_object(m_us, m_fg.Row);
            f206_v_gd_cong_tac_de v_fDE = new f206_v_gd_cong_tac_de();
            v_fDE.display_for_update(m_us);
            load_data_2_grid();
            m_fg.Select(currentRow,  (int)e_col_Number.MA_QUYET_DINH);
        }

        private void delete_v_gd_cong_tac()
        {
            if (!CGridUtils.IsThere_Any_NonFixed_Row(m_fg)) return;
            if (!CGridUtils.isValid_NonFixed_RowIndex(m_fg, m_fg.Row)) return;
            if (BaseMessages.askUser_DataCouldBeDeleted(8) != BaseMessages.IsDataCouldBeDeleted.CouldBeDeleted) return;
            US_V_GD_CONG_TAC v_us = new US_V_GD_CONG_TAC();
            grid2us_object(v_us, m_fg.Row);
            try
            {
                v_us.BeginTransaction();
                v_us.DeleteByID(v_us.dcID_QUYET_DINH);
                v_us.CommitTransaction();
                m_fg.Rows.Remove(m_fg.Row);
                BaseMessages.MsgBox_Infor("Xóa thành công.");
                m_fg.Select(1, (int)e_col_Number.MA_QUYET_DINH);
            }
            catch (Exception v_e)
            {
                v_us.Rollback();
                throw v_e;
            }
        }

        private void view_v_gd_cong_tac()
        {
            if (!CGridUtils.IsThere_Any_NonFixed_Row(m_fg)) return;
            if (!CGridUtils.isValid_NonFixed_RowIndex(m_fg, m_fg.Row)) return;
            grid2us_object(m_us, m_fg.Row);
            //	f206_v_gd_cong_tac_DE v_fDE = new f206_v_gd_cong_tac_DE();			
            //	v_fDE.display(m_us);
        }

        private void auto_suggest_text()
        {
            int count = m_ds.Tables["V_GD_CONG_TAC"].Rows.Count;
            AutoCompleteStringCollection v_acsc_search = new AutoCompleteStringCollection();
            foreach (DataRow dr in m_ds.V_GD_CONG_TAC)
            {
                v_acsc_search.Add(dr[V_GD_CONG_TAC.MA_NV].ToString());
                v_acsc_search.Add(dr[V_GD_CONG_TAC.HO_DEM].ToString() + " " + dr[V_GD_TRANG_THAI_LAO_DONG.TEN].ToString());
                v_acsc_search.Add(dr[V_GD_CONG_TAC.TEN].ToString());
                v_acsc_search.Add(dr[V_GD_CONG_TAC.DIA_DIEM].ToString());
                v_acsc_search.Add(dr[V_GD_CONG_TAC.MA_QUYET_DINH].ToString());
            }
            m_txt_tim_kiem.AutoCompleteCustomSource = v_acsc_search;
        }

        private bool view_quyet_dinh_saved()
        {
            if (!CGridUtils.IsThere_Any_NonFixed_Row(m_fg)) return true;
            if (!CGridUtils.isValid_NonFixed_RowIndex(m_fg, m_fg.Row)) return true;
            if (m_fg.Rows[m_fg.Row].IsNode) return false;
            grid2us_object(m_us, m_fg.Row);
            if (m_fg.Col == (int)e_col_Number.MA_QUYET_DINH)
            {
                US_DM_QUYET_DINH v_us = new US_DM_QUYET_DINH(m_us.dcID_QUYET_DINH);
                if (v_us.strLINK != "")
                {
                    f206_v_gd_cong_tac_view_document v_frm = new f206_v_gd_cong_tac_view_document();
                    v_frm.display(v_us.strLINK);
                }
                else
                {
                    BaseMessages.MsgBox_Error("Chưa có file đính kèm.");
                }
            }
            return false;
        }

